import {
	createFormHook,
	createFormHookContexts,
	revalidateLogic,
	useStore,
} from "@tanstack/react-form";
import type { VariantProps } from "class-variance-authority";
import * as React from "react";
import { Button, type buttonVariants } from "@/components/ui/button";
import {
	Field as DefaultField,
	FieldError as DefaultFieldError,
	FieldSet as DefaultFieldSet,
	FieldContent,
	FieldDescription,
	FieldGroup,
	FieldLabel,
	FieldLegend,
	FieldSeparator,
	FieldTitle,
	fieldVariants,
} from "@/components/ui/field";
import {
	InputGroup,
	InputGroupAddon,
	InputGroupInput,
} from "@/components/ui/input-group";
import { cn } from "@/lib/utils";
import { Spinner } from "@/components/ui/spinner";

const {
	fieldContext,
	formContext,
	useFieldContext: _useFieldContext,
	useFormContext,
} = createFormHookContexts();

const { useAppForm, withForm, withFieldGroup } = createFormHook({
	fieldContext,
	formContext,
	fieldComponents: {
		Field,
		FieldError,
		FieldSet,
		FieldContent,
		FieldDescription,
		FieldGroup,
		FieldLabel,
		FieldLegend,
		FieldSeparator,
		FieldTitle,
		InputGroup,
		InputGroupAddon,
		InputGroupInput,
	},
	formComponents: {
		SubmitButton,
		StepButton,
		FieldLegend,
		FieldDescription,
		FieldSeparator,
		Form,
	},
});

type FormItemContextValue = {
	id: string;
};

const FormItemContext = React.createContext<FormItemContextValue>(
	{} as FormItemContextValue,
);

function FieldSet({
	className,
	children,
	...props
}: React.ComponentProps<"fieldset">) {
	const id = React.useId();

	return (
		<FormItemContext.Provider value={{ id }}>
			<DefaultFieldSet className={cn("grid gap-1", className)} {...props}>
				{children}
			</DefaultFieldSet>
		</FormItemContext.Provider>
	);
}

const useFieldContext = () => {
	const { id } = React.useContext(FormItemContext);
	const { name, store, ...fieldContext } = _useFieldContext();

	const errors = useStore(store, (state) => state.meta.errors);
	if (!fieldContext) {
		throw new Error("useFieldContext should be used within <FormItem>");
	}

	return {
		id,
		name,
		formItemId: `${id}-form-item`,
		formDescriptionId: `${id}-form-item-description`,
		formMessageId: `${id}-form-item-message`,
		errors,
		store,
		...fieldContext,
	};
};

function Field({
	children,
	...props
}: React.ComponentProps<"div"> & VariantProps<typeof fieldVariants>) {
	const { errors, formItemId, formDescriptionId, formMessageId } =
		useFieldContext();

	return (
		<DefaultField
			data-invalid={!!errors.length}
			id={formItemId}
			aria-describedby={
				!errors.length
					? `${formDescriptionId}`
					: `${formDescriptionId} ${formMessageId}`
			}
			aria-invalid={!!errors.length}
			{...props}
		>
			{children}
		</DefaultField>
	);
}

function FieldError({ className, ...props }: React.ComponentProps<"p">) {
	const { errors, formMessageId } = useFieldContext();
	const body = errors.length ? String(errors.at(0)?.message ?? "") : "";
	if (!body) return null;
	return (
		<DefaultFieldError
			data-slot="form-message"
			id={formMessageId}
			className={cn("text-destructive text-sm", className)}
			{...props}
			errors={body ? [{ message: body }] : []}
		/>
	);
}

function Form({
	children,
	...props
}: Omit<React.ComponentPropsWithoutRef<"form">, "onSubmit" & "noValidate"> & {
	children?: React.ReactNode;
}) {
	const form = useFormContext();
	const handleSubmit = React.useCallback(
		(e: React.FormEvent<HTMLFormElement>) => {
			e.preventDefault();
			e.stopPropagation();
			form.handleSubmit();
		},
		[form],
	);
	return (
		<form
			onSubmit={handleSubmit}
			className={cn(
				"flex flex-col p-2 md:p-5 w-full mx-auto gap-2",
				props.className,
			)}
			noValidate
			{...props}
		>
			{children}
		</form>
	);
}

function SubmitButton({
	label,
	className,
	size,
	...props
}: React.ComponentProps<"button"> &
	VariantProps<typeof buttonVariants> & {
		label: string;
	}) {
	const form = useFormContext();
	return (
		<form.Subscribe selector={(state) => state.isSubmitting}>
			{(isSubmitting) => (
				<Button
					className={className}
					size={size}
					type="submit"
					disabled={isSubmitting}
					{...props}
				>
					{isSubmitting && <Spinner />}
					{label}
				</Button>
			)}
		</form.Subscribe>
	);
}

function StepButton({
	label,
	handleMovement,
	...props
}: React.ComponentProps<"button"> &
	VariantProps<typeof buttonVariants> & {
		label: React.ReactNode | string;
		handleMovement: () => void;
	}) {
	return (
		<Button
			size="sm"
			variant="ghost"
			type="button"
			onClick={handleMovement}
			{...props}
		>
			{label}
		</Button>
	);
}

export {
	revalidateLogic,
	useAppForm,
	useFieldContext,
	useFormContext,
	withFieldGroup,
	withForm,
};

import type { AnyFormApi } from "@tanstack/react-form";
import { useCallback, useState } from "react";
import type { ZodObject } from "zod";

/**
 * Options for handling cancel/back actions
 */
type HandleCancelOrBackOpts = {
	onBack?: VoidFunction;
	onCancel?: VoidFunction;
};

/**
 * State of the current step
 */
type StepState = {
	value: number;
	count: number;
	goToNextStep: () => void;
	goToPrevStep: () => void;
	isCompleted: boolean;
};

/**
 * Hook for managing multi-step form navigation and validation
 *
 * @param schemas - Array of Zod schemas for each step
 * @returns Object with stepper state and methods
 */
export function useFormStepper(schemas: ZodObject[]) {
	const stepCount = schemas.length;
	const [currentStep, setCurrentStep] = useState(1); // Start from 1

	const goToNextStep = useCallback(() => {
		setCurrentStep((prev) => Math.min(prev + 1, stepCount));
	}, [stepCount]);

	const goToPrevStep = useCallback(() => {
		setCurrentStep((prev) => Math.max(prev - 1, 1));
	}, []);

	const step: StepState = {
		value: currentStep,
		count: stepCount,
		goToNextStep,
		goToPrevStep,
		isCompleted: currentStep === stepCount,
	};

	const currentValidator = schemas[currentStep - 1]; // Convert to 0-based for array access
	const isFirstStep = currentStep === 1;

	const triggerFormGroup = async (form: AnyFormApi) => {
		const result = currentValidator.safeParse(form.state.values);
		if (!result.success) {
			await form.handleSubmit({ step: String(currentStep) });
			return result;
		}

		return result;
	};

	const handleNextStepOrSubmit = async (form: AnyFormApi) => {
		const result = await triggerFormGroup(form);
		if (!result.success) {
			return;
		}

		if (currentStep < stepCount) {
			goToNextStep();
			return;
		}

		if (currentStep === stepCount) {
			form.handleSubmit();
		}
	};

	const handleCancelOrBack = (opts?: HandleCancelOrBackOpts) => {
		if (isFirstStep || step.isCompleted) {
			opts?.onCancel?.();
			return;
		}

		if (currentStep > 1) {
			opts?.onBack?.();
			goToPrevStep();
		}
	};

	return {
		step, // Current step state
		currentStep, // Current step number (1-based)
		isFirstStep, // Whether current step is the first step
		currentValidator, // Zod schema for current step
		triggerFormGroup, // Validate current step fields
		handleNextStepOrSubmit, // Handle next/submit action
		handleCancelOrBack, // Handle back/cancel action
	};
}
